app.beginUndoGroup("Add Background Text");

function truncateString(str, num) {
  if (str.length <= num) {
    return str
  }
  return str.slice(0, num) + ' ...'
}

var curComp = app.project.activeItem;

// Text Layer
var addTextBool = _ bool: Create Text? _;
var curText = _ layer: Text Layer _;

if(curComp && addTextBool) {
  curText = curComp.layers.addText(_ string: Your Text _);
}

// Padding
var paddingTop = _ number: Padding Top _;
var paddingBottom = _ number: Padding Bottom _;
var paddingLeft = _ number: Padding Left _;
var paddingRight = _ number: Padding Right _;

// Color
var fillColor = _ color: Fill Color _;
var strokeColor = _ color: Stroke Color _;

var sliderControl = "ADBE Slider Control";

var groupName = "Rectangle Box";
var effectsName = "Effects";
var rectanglePathName = "Rectangle Path 1";

if (curComp && curText) {
	var shapeLayer = curComp.layers.addShape();
	shapeLayer.name = "Text Box";
	var shapeGroup = shapeLayer.property("Contents").addProperty("ADBE Vector Group");
	shapeGroup.name = groupName;
  shapeGroup.property("Contents").addProperty("ADBE Vector Shape - Rect");

	var fill = shapeGroup.property("Contents").addProperty("ADBE Vector Graphic - Fill");
	fill.Color.setValue(fillColor);
	var stroke = shapeGroup.property("Contents").addProperty("ADBE Vector Graphic - Stroke");
	stroke.Color.setValue(strokeColor);

	// Slider Control
	var controllerTotalPad = shapeLayer.Effects.addProperty(sliderControl);
  controllerTotalPad.name = "Total Padding";
  shapeLayer.property(effectsName).property(controllerTotalPad.name)(1).expression = "effect(\"Padding Top & Bottom\")(1) + effect(\"Padding Left & Right\")(1)";

  var controllerPadTopBottom = shapeLayer.Effects.addProperty(sliderControl);
  controllerPadTopBottom.name = "Padding Top & Bottom";
  controllerPadTopBottom.Slider.setValue(paddingTop);

  var controllerPadLeftRight = shapeLayer.Effects.addProperty(sliderControl);
  controllerPadLeftRight.name = "Padding Left & Right";
  controllerPadLeftRight.Slider.setValue(paddingLeft);

  var totalPaddingHeight = "effect(\"Padding Top & Bottom\")(1)";
  var totalPaddingWidth = "effect(\"Padding Left & Right\")(1)";


	// Expression Sizes & Position
	var expressionSize = "x = thisComp.layer(\"" + curText.name + "\").sourceRectAtTime().width" + 
	";y = thisComp.layer(\"" + curText.name + "\").sourceRectAtTime().height;" + 
	"[x + " + totalPaddingWidth + ", y + " + totalPaddingHeight + "];";

	var expressionPos = "s=thisComp.layer(\"" + curText.name + "\");" +
                      "w=s.sourceRectAtTime().width/2;" +
                      "h=s.sourceRectAtTime().height/2;" + 
                      "l=s.sourceRectAtTime().left;" +
                      "t=s.sourceRectAtTime().top;" +
                      "[w+l,h+t];";

	// Set expression to shapeLayer
	shapeLayer.content(groupName).content(rectanglePathName).size.expression = expressionSize;
	shapeLayer.content(groupName).content(rectanglePathName).position.expression = expressionPos;


	// If Using existing text layer
	if(!addTextBool){
	  var posText = curComp.layer(curText.name).property("Position").value;
	  shapeLayer.property("Position").setValue([posText[0], posText[1]]);
	}

	// Name Shape layer based on text
	shapeLayer.name += " - " + truncateString(curText.name, 10);

	// Set parent shape layer to text layer
	curComp.layer(shapeLayer.name).parent = curText;

	// Set Position of Text to be center of comp
	curText.property("position").setValue([curComp.width/2, curComp.height/2]);

	// Move to last index
	shapeLayer.moveToEnd();
}

// Deselect All Layers
app.executeCommand(2004);

app.endUndoGroup();
