function findControllerName(name) {
	var layerName = "";
	var controller = {
		point3D: "ADBE Point3D Control",
		angle: "ADBE Angle Control",
		checkbox: "ADBE Checkbox Control",
		color: "ADBE Color Control",
		layer: "ADBE Layer Control",
		point: "ADBE Point Control",
		slider: "ADBE Slider Control"
	};

	for (var control in controller) {
		var sanitize = control.replace(/ADBE/g).replace(/Control/g).trim();
		var nameReg = new RegExp(name, "g");
		if (sanitize.match(nameReg)) {
			layerName = controller[control];
			break;
		}
	}

	return layerName;
}

var comp = app.project.selection;
var MUTEAUDIO = _ bool: Mute Audio ? _;
var compSec = _ number *: Number of seconds on each Divide Compositions _;
var fastForward = _ bool: Enable fast forward ? _;
var fastForwardDifference = _ number: Time in seconds to fast forward _;
var rotateValue = _ number: Rotate Compositions ? (HINTS: 90, 180, 270) _;
var timeRemappingPropertyName = "ADBE Time Remapping";

var ROTATION = {
	VERTICAL: 90,
	HORIZONTAL: 180,
	INVERTVERTICAL: 270
}

if (comp.length === 0) {
	alert("You didn't select any compositions in Project Panel. Please select atleast one");
} else {
	app.beginUndoGroup("Divide Compositions");
	main();
	app.endUndoGroup();
}

function rotation(comp, layer, rotation) {
	var width = comp.width;
	var height = comp.height;
	var posX = layer.Transform.property("Position").value[0];
	var posY = layer.Transform.property("Position").value[1];
	if (ROTATION.VERTICAL == rotation) {
		comp.width = height;
		comp.height = width;
		layer.Transform.property("Rotation").setValue(rotation);
		layer.Transform.property("Position").setValue([posY, posX]);
		return;
	} else if (ROTATION.INVERTVERTICAL == rotation) {
		comp.width = height;
		comp.height = width;
		layer.Transform.property("Rotation").setValue(rotation);
		layer.Transform.property("Position").setValue([posY, posX]);
		return;
	} else if (ROTATION.HORIZONTAL == rotation) {
		layer.Transform.property("Rotation").setValue(rotation);
		return;
	} else {
		write("ERROR ROTATE: " + comp.name);
	}
}

function rotateName(compName, rotation) {
	if (ROTATION.VERTICAL == rotation) {
		return compName + " - ROTATE " + rotation + "°";
	} else if (ROTATION.INVERTVERTICAL == rotation) {
		return compName + " - ROTATE " + rotation + "°";
	} else if (ROTATION.HORIZONTAL == rotation) {
		return compName + " - ROTATE " + rotation + "°";
	} else {
		return compName;
	}
}

function main() {
	for (var i = 0; i < comp.length; i++) {
		var compName = comp[i].name.replace(/\.[^\/\.]+$/, "");
		if (rotateValue) {
			compName = rotateName(compName, rotateValue);
		}
		var folder = app.project.items.addFolder(compName);
		var remaining = calculateDivide(fastForward, compSec, fastForwardDifference);
		var compTimeDivide = comp[i].duration / remaining;
		var startTime = 0;
		var lastTime = comp[i].duration;
		for (var numComp = 0; numComp < compTimeDivide; numComp++) {
			// If it reaches limit, reset fastForwardStart & fastForwardEnd value
			if (lastTime < 0) {
				fastForwardStart = undefined;
				fastForwardEnd = undefined;
				break;
			}

			// Adding new comp in a folder
			var newComp = folder.items.addComp(compName + "-" + numComp, comp[i].width, comp[i].height, comp[i].pixelAspect, compSec, comp[i].frameRate);
			newComp.layers.add(comp[i]);
			var layer = newComp.layer(comp[i].name);
			layer.startTime -= startTime;

			if (rotateValue) {
				rotation(newComp, layer, rotateValue);
			}

			// If user wants to mute audio
			if (MUTEAUDIO) {
				layer.audioEnabled = false;
			}

			// If fast forward enabled
			if (fastForward && layer.canSetTimeRemapEnabled) {
				var fastForwardStart = fastForwardEnd ? fastForwardEnd : startTime;

				// Calculate at the end of fastForwardEnd value
				if (numComp > 1 && fastForwardEnd) {
					fastForwardEnd = fastForwardEnd + fastForwardDifference;
				} else {
					var fastForwardEnd = startTime + compSec + fastForwardDifference;
				}

				// If it reaches the maximum of compositions duration
				if (fastForwardEnd > comp[i].duration) {
					fastForwardEnd = comp[i].duration;
				}


				// Time remapping starts
				layer.timeRemapEnabled = true;
				var newFirstKeyIndex = layer.property(timeRemappingPropertyName).addKey(newComp.displayStartTime);
				var newLastKeyIndex = layer.property(timeRemappingPropertyName).addKey(newComp.displayStartTime + newComp.duration);
				layer.property(timeRemappingPropertyName).setValueAtKey(newFirstKeyIndex, fastForwardStart);
				layer.property(timeRemappingPropertyName).setValueAtKey(newLastKeyIndex, fastForwardEnd);

				// Will decrease it lastTime to exit the loop
				lastTime -= fastForwardDifference;
			}

			// Will increase startTime to continue doing adding newComp at the number of seconds input
			startTime += compSec;
		}
	}
}


// Function to calculate remaining of time based on number of compositions set.
function calculateDivide(enable, compSec, difference) {
	if (enable) {
		var remaining = compSec - difference;
		if (remaining > 0) {
			return remaining
		} else {
			return 1;
		}
	} else {
		return compSec;
	}
}

