function addController(controlLayer, targerLayer, effect, controller) {
	var AE_Controller = findControllerName(controller.controllerType);
	// Get Value from target layer
	if (effect.effectName){
	  var getValue = targerLayer.Effects.property(effect.effectType)(effect.effectName).value;
	} else {
	  var getValue = targerLayer.Effects.property(effect.effectType)(1).value;
	}

	var slider_control = controlLayer.Effects.addProperty(AE_Controller);
	if (controller.controllerName && controller.controllerName.length > 0) {
		slider_control.name = controller.controllerName;
	}

	// Apply value on slider controller
	slider_control.property(1).setValue(getValue);

	// Set expression on targetLayer
	if (effect.effectName) {
		targerLayer.Effects.property(effect.effectType)(effect.effectName).expression = "thisComp.layer(\"" + controlLayer.name + "\").effect(\"" + slider_control.name + "\")(1)";
	} else {
		targerLayer.Effects.property(effect.effectType)(1).expression = "thisComp.layer(\"" + controlLayer.name + "\").effect(\"" + slider_control.name + "\")(1)";
	}
}

function main() {
	for (var numComp = 0; numComp < selections.length; numComp++) {
		var naming = removeExtensionName(selections[numComp].name);
		if (customName && customName.length > 0) {
			naming = !postfix ? customName + " - " + removeExtensionName(selections[numComp].name) : removeExtensionName(selections[numComp].name) + " - " + customName;
		}
		var newComp = app.project.items.addComp(naming, selections[numComp].width, selections[numComp].height, selections[numComp].pixelAspect, selections[numComp].duration, selections[numComp].frameRate);
		newComp.layers.add(selections[numComp]);
		var layer = newComp.layer(selections[numComp].name);
		var secondLayer = layer.duplicate();
		secondLayer.blendingMode = SOFT_LIGHT;

		if (nullController) {
			var NULL_LAYER = addNullLayer(newComp, "CONTROLLER");
		}

		try {
			var DARK = addAdjustmentLayer(newComp, selections[numComp], "DARK");
			var DARK_MASK1 = DARK.Effects.addProperty(unsharpMask);
			DARK_MASK1.property("radius").setValue(DARK_MaskRadius);
			DARK_MASK1.property("amount").setValue(DARK_MaskAmount);

			var LIGHT = addAdjustmentLayer(newComp, selections[numComp], "LIGHT");
			LIGHT.blendingMode = LIGHTEN;

			// Start with mask 2 first because my format is always like that
			var LIGHT_MASK2 = LIGHT.Effects.addProperty(unsharpMask);
			LIGHT_MASK2.property("radius").setValue(LIGHT_MaskRadius * 2);
			LIGHT_MASK2.property("amount").setValue(LIGHT_MaskAmount * 2);

			var LIGHT_MASK1 = LIGHT.Effects.addProperty(unsharpMask);
			LIGHT_MASK1.property("radius").setValue(LIGHT_MaskRadius);
			LIGHT_MASK1.property("amount").setValue(LIGHT_MaskAmount);

			var HUE = addAdjustmentLayer(newComp, selections[numComp], "HUE");
			var HUE_EFFECT = HUE.Effects.addProperty(hue);

			if (NULL_LAYER) {
				// 1
				addController(NULL_LAYER, DARK, {
					effectType: unsharpMask
				}, {
					controllerType: "slider",
					controllerName: "Unsharp Mask (Dark)"
				});
				addController(NULL_LAYER, DARK, {
				  effectType:"Unsharp Mask",
				  effectName:"Radius"
				}, {
				  controllerType: "slider",
				  controllerName: "Unsharp Mask Radius (Dark)"
				})
				// 2
				addController(NULL_LAYER, LIGHT, {
					effectType: "Unsharp Mask",
					effectName: "Amount"
				}, {
					controllerType: "slider",
					controllerName: "Unsharp Mask (Low Light)"
				});
				addController(NULL_LAYER, LIGHT, {
					effectType: "Unsharp Mask",
					effectName: "Radius"
				}, {
					controllerType: "slider",
					controllerName: "Unsharp Mask Radius (High Light)"
				});
				// 3
				addController(NULL_LAYER, LIGHT, {
					effectType: "Unsharp Mask 2",
					effectName: "Amount"
				}, {
					controllerType: "slider",
					controllerName: "Unsharp Mask (Low Light)"
				});
				addController(NULL_LAYER, LIGHT, {
					effectType: "Unsharp Mask 2",
					effectName: "Radius"
				}, {
					controllerType: "slider",
					controllerName: "Unsharp Mask Radius (High Light)"
				});
			}

			if (BRIGHTNESS_CONTROL) {
				var BRIGHTNESS_LAYER = addBrightnessControl(newComp, selections[numComp], "BRIGHTNESS");
				BRIGHTNESS_LAYER.blendingMode = LIGHTEN;
				BRIGHTNESS_LAYER.Effects.property(brightness).property("Brightness").setValue(BRIGHTNESS_VALUE ? BRIGHTNESS_VALUE : 0);
				BRIGHTNESS_LAYER.Effects.property(brightness).property("Contrast").setValue(CONTRAST_VALUE ? CONTRAST_VALUE : 0);

				if (NULL_LAYER) {
					// 1
					addController(NULL_LAYER, BRIGHTNESS_LAYER, {
						effectType: "Brightness & Contrast",
						effectName: "Brightness"
					}, {
						controllerType: "slider",
						controllerName: "Brightness"
					})
					// 2
					addController(NULL_LAYER, BRIGHTNESS_LAYER, {
						effectType: "Brightness & Contrast",
						effectName: "Contrast"
					}, {
						controllerType: "slider",
						controllerName: "Contrast"
					})
				}
			}
		} catch (e) {
			alert(e);
		}

  	if (NULL_LAYER) {
  		NULL_LAYER.moveToBeginning()
  	}
	}
}

function addBrightnessControl(comp, selectionComp, layerName) {
	var BRIGHTNESS_ADJUSTMENT = addAdjustmentLayer(comp, selectionComp, layerName);
	BRIGHTNESS_ADJUSTMENT.Effects.addProperty(brightness);
	return BRIGHTNESS_ADJUSTMENT;
}

function addAdjustmentLayer(comp, selectionComp, layerName) {
	var ADJUSTMENTLAYER = comp.layers.addSolid([1, 1, 1], layerName, selectionComp.width, selectionComp.height, selectionComp.pixelAspect, selectionComp.duration);
	// Enable Adjustment layer
	ADJUSTMENTLAYER.adjustmentLayer = true;
	return ADJUSTMENTLAYER;
}

function addNullLayer(comp, layerName) {
	var NULL_LAYER = comp.layers.addNull();
	NULL_LAYER.name = layerName
	return NULL_LAYER;
}

